import type { Express } from "express";
import { createServer, type Server } from "http";
import multer from "multer";
import { storage } from "./storage";
import { generateSummary, promptTemplates } from "./services/ai";
import { sendSummaryEmail } from "./services/email";
import { insertSummarySchema, insertEmailShareSchema } from "@shared/schema";

// Configure multer for file uploads
const upload = multer({
  storage: multer.memoryStorage(),
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB limit
  },
  fileFilter: (req, file, cb) => {
    const allowedTypes = ['text/plain', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
    if (allowedTypes.includes(file.mimetype) || file.originalname.endsWith('.txt') || file.originalname.endsWith('.docx')) {
      cb(null, true);
    } else {
      cb(new Error('Only .txt and .docx files are allowed'));
    }
  }
});

export async function registerRoutes(app: Express): Promise<Server> {

  // Get prompt templates
  app.get("/api/templates", (req, res) => {
    res.json(promptTemplates);
  });

  // Upload and process transcript
  app.post("/api/upload", upload.single('transcript'), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ message: "No file uploaded" });
      }

      let transcriptText = "";

      if (req.file.mimetype === 'text/plain' || req.file.originalname.endsWith('.txt')) {
        transcriptText = req.file.buffer.toString('utf-8');
      } else if (req.file.mimetype === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document' || req.file.originalname.endsWith('.docx')) {
        // For simplicity, we'll treat .docx as text for now
        // In a real implementation, you'd use a library like mammoth to extract text from .docx
        transcriptText = req.file.buffer.toString('utf-8');
      }

      if (!transcriptText.trim()) {
        return res.status(400).json({ message: "File appears to be empty or unreadable" });
      }

      res.json({
        filename: req.file.originalname,
        size: req.file.size,
        content: transcriptText
      });
    } catch (error) {
      console.error("Upload error:", error);
      res.status(500).json({ message: "Failed to process uploaded file" });
    }
  });

  // Generate AI summary
  app.post("/api/generate-summary", async (req, res) => {
    try {
      const { transcript, prompt } = req.body;

      if (!transcript || !prompt) {
        return res.status(400).json({ message: "Transcript and prompt are required" });
      }

      const summary = await generateSummary(transcript, prompt);

      // Save to storage
      const savedSummary = await storage.createSummary({
        originalTranscript: transcript,
        customPrompt: prompt,
        generatedSummary: summary,
        editedSummary: null
      });

      res.json({
        summaryId: savedSummary.id,
        summary: summary
      });
    } catch (error) {
      console.error("Summary generation error:", error);
      res.status(500).json({ 
        message: error instanceof Error ? error.message : "Failed to generate summary" 
      });
    }
  });

  // Update summary (for edits)
  app.patch("/api/summaries/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const { editedSummary } = req.body;

      if (!editedSummary) {
        return res.status(400).json({ message: "Edited summary is required" });
      }

      const updated = await storage.updateSummary(id, { editedSummary });

      if (!updated) {
        return res.status(404).json({ message: "Summary not found" });
      }

      res.json(updated);
    } catch (error) {
      console.error("Summary update error:", error);
      res.status(500).json({ message: "Failed to update summary" });
    }
  });

  // Get summary
  app.get("/api/summaries/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const summary = await storage.getSummary(id);

      if (!summary) {
        return res.status(404).json({ message: "Summary not found" });
      }

      res.json(summary);
    } catch (error) {
      console.error("Get summary error:", error);
      res.status(500).json({ message: "Failed to retrieve summary" });
    }
  });

  // Send email
  app.post("/api/send-email", async (req, res) => {
    try {
      const validatedData = insertEmailShareSchema.parse(req.body);

      const summary = await storage.getSummary(validatedData.summaryId);
      if (!summary) {
        return res.status(404).json({ message: "Summary not found" });
      }

      const summaryContent = summary.editedSummary || summary.generatedSummary;

      await sendSummaryEmail({
        recipients: validatedData.recipients,
        subject: validatedData.subject,
        message: validatedData.message || undefined,
        summaryHtml: summaryContent
      });

      // Save email share record
      await storage.createEmailShare(validatedData);

      res.json({ message: "Email sent successfully" });
    } catch (error) {
      console.error("Email sending error:", error);
      res.status(500).json({ 
        message: error instanceof Error ? error.message : "Failed to send email" 
      });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type Summary, type InsertSummary, type EmailShare, type InsertEmailShare } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  createSummary(summary: InsertSummary): Promise<Summary>;
  getSummary(id: string): Promise<Summary | undefined>;
  updateSummary(id: string, updates: Partial<InsertSummary>): Promise<Summary | undefined>;
  createEmailShare(emailShare: InsertEmailShare): Promise<EmailShare>;
  getEmailSharesBySummaryId(summaryId: string): Promise<EmailShare[]>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private summaries: Map<string, Summary>;
  private emailShares: Map<string, EmailShare>;

  constructor() {
    this.users = new Map();
    this.summaries = new Map();
    this.emailShares = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async createSummary(insertSummary: InsertSummary): Promise<Summary> {
    const id = randomUUID();
    const summary: Summary = { 
      ...insertSummary,
      editedSummary: insertSummary.editedSummary ?? null,
      id,
      createdAt: new Date()
    };
    this.summaries.set(id, summary);
    return summary;
  }

  async getSummary(id: string): Promise<Summary | undefined> {
    return this.summaries.get(id);
  }

  async updateSummary(id: string, updates: Partial<InsertSummary>): Promise<Summary | undefined> {
    const existing = this.summaries.get(id);
    if (!existing) return undefined;

    const updated: Summary = { ...existing, ...updates };
    this.summaries.set(id, updated);
    return updated;
  }

  async createEmailShare(insertEmailShare: InsertEmailShare): Promise<EmailShare> {
    const id = randomUUID();
    const emailShare: EmailShare = { 
      ...insertEmailShare,
      message: insertEmailShare.message ?? null,
      recipients: insertEmailShare.recipients as string[],
      id,
      sentAt: new Date()
    };
    this.emailShares.set(id, emailShare);
    return emailShare;
  }

  async getEmailSharesBySummaryId(summaryId: string): Promise<EmailShare[]> {
    return Array.from(this.emailShares.values()).filter(
      (emailShare) => emailShare.summaryId === summaryId
    );
  }
}

export const storage = new MemStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, json } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const summaries = pgTable("summaries", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  originalTranscript: text("original_transcript").notNull(),
  customPrompt: text("custom_prompt").notNull(),
  generatedSummary: text("generated_summary").notNull(),
  editedSummary: text("edited_summary"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const emailShares = pgTable("email_shares", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  summaryId: varchar("summary_id").notNull(),
  recipients: json("recipients").$type<string[]>().notNull(),
  subject: text("subject").notNull(),
  message: text("message"),
  sentAt: timestamp("sent_at").defaultNow().notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export const insertSummarySchema = createInsertSchema(summaries).pick({
  originalTranscript: true,
  customPrompt: true,
  generatedSummary: true,
  editedSummary: true,
});

export const insertEmailShareSchema = createInsertSchema(emailShares).pick({
  summaryId: true,
  recipients: true,
  subject: true,
  message: true,
}).extend({
  recipients: z.array(z.string()),
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type InsertSummary = z.infer<typeof insertSummarySchema>;
export type Summary = typeof summaries.$inferSelect;
export type InsertEmailShare = z.infer<typeof insertEmailShareSchema>;
export type EmailShare = typeof emailShares.$inferSelect;

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import Home from "@/pages/home";
import NotFound from "@/pages/not-found";

function Router() {
  return (
    <Switch>
      <Route path="/" component={Home} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

