import OpenAI from "openai";

// the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY || process.env.OPENAI_API_KEY_ENV_VAR || "default_key" 
});

export async function generateSummary(transcript: string, customPrompt: string): Promise<string> {
  try {
    const prompt = `${customPrompt}

Please process the following meeting transcript and generate a summary based on the instructions above:

${transcript}

Format the output as clean HTML that can be displayed in a web page. Use appropriate tags like <h3>, <h4>, <ul>, <li>, <p>, <strong> for structure and formatting.`;

    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: "You are an expert meeting summarizer. Generate clear, well-structured summaries based on the provided instructions. Always format your response as clean HTML."
        },
        {
          role: "user",
          content: prompt
        }
      ],
      temperature: 0.3,
      max_tokens: 2000
    });

    const summary = response.choices[0].message.content;
    if (!summary) {
      throw new Error("No summary generated from AI");
    }

    return summary;
  } catch (error) {
    console.error("Error generating AI summary:", error);
    throw new Error(`Failed to generate summary: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

export const promptTemplates = {
  "Executive Summary Format": "Create an executive summary with key highlights, decisions made, and strategic implications. Format with clear sections and bullet points for easy executive review.",
  "Action Items Only": "Extract only action items, deadlines, and assigned responsibilities. List each item with the person responsible and due date clearly identified.",
  "Project Status Update": "Summarize project progress, milestones achieved, upcoming deliverables, and any blockers or risks identified. Focus on status and next steps.",
  "Key Decisions & Outcomes": "Focus on decisions made during the meeting, outcomes achieved, and next steps. Include any voting results or consensus reached."
};

server/services/email.ts.content:
import nodemailer from 'nodemailer';

// Configure email transporter
const transporter = nodemailer.createTransport({
  host: process.env.SMTP_HOST || 'smtp.gmail.com',
  port: parseInt(process.env.SMTP_PORT || '587'),
  secure: false, // true for 465, false for other ports
  auth: {
    user: process.env.SMTP_USER || process.env.EMAIL_USER,
    pass: process.env.SMTP_PASS || process.env.EMAIL_PASS,
  },
});

export interface EmailOptions {
  recipients: string[];
  subject: string;
  message?: string;
  summaryHtml: string;
}

export async function sendSummaryEmail(options: EmailOptions): Promise<void> {
  const { recipients, subject, message, summaryHtml } = options;

  try {
    const htmlContent = `
      <!DOCTYPE html>
      <html>
      <head>
        <meta charset="utf-8">
        <style>
          body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif; line-height: 1.6; color: #333; }
          .container { max-width: 800px; margin: 0 auto; padding: 20px; }
          .header { border-bottom: 2px solid #2563EB; padding-bottom: 15px; margin-bottom: 20px; }
          .summary { background: #f8fafc; padding: 20px; border-radius: 8px; margin: 20px 0; }
          .footer { text-align: center; font-size: 12px; color: #666; margin-top: 30px; border-top: 1px solid #ddd; padding-top: 15px; }
          h1 { color: #2563EB; margin: 0; }
          h3, h4 { color: #1e293b; }
          ul { padding-left: 20px; }
          li { margin-bottom: 5px; }
        </style>
      </head>
      <body>
        <div class="container">
          <div class="header">
            <h1>ðŸ¤– AI Meeting Summary</h1>
          </div>

          ${message ? `<p><strong>Message:</strong> ${message}</p>` : ''}

          <div class="summary">
            ${summaryHtml}
          </div>

          <div class="footer">
            <p>This summary was generated using AI Meeting Summarizer</p>
            <p>Generated on ${new Date().toLocaleDateString()}</p>
          </div>
        </div>
      </body>
      </html>
    `;

    const mailOptions = {
      from: process.env.SMTP_USER || process.env.EMAIL_USER,
      to: recipients.join(', '),
      subject: subject,
      html: htmlContent,
    };

    await transporter.sendMail(mailOptions);
  } catch (error) {
    console.error('Error sending email:', error);
    throw new Error(`Failed to send email: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

client/src/components/file-upload.tsx.content:
import { useState, useRef } from "react";
import { Upload, FileText, X } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";

interface FileUploadProps {
  onFileUploaded: (content: string, filename: string) => void;
  onNext: () => void;
}

export function FileUpload({ onFileUploaded, onNext }: FileUploadProps) {
  const [uploadedFile, setUploadedFile] = useState<{ name: string; size: number } | null>(null);
  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const handleFileUpload = async (file: File) => {
    if (!file) return;

    // Validate file type
    const validTypes = ['text/plain', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
    const validExtensions = ['.txt', '.docx'];
    const isValidType = validTypes.includes(file.type) || validExtensions.some(ext => file.name.toLowerCase().endsWith(ext));

    if (!isValidType) {
      toast({
        title: "Invalid file type",
        description: "Please upload a .txt or .docx file",
        variant: "destructive"
      });
      return;
    }

    // Validate file size (10MB limit)
    if (file.size > 10 * 1024 * 1024) {
      toast({
        title: "File too large",
        description: "Please upload a file smaller than 10MB",
        variant: "destructive"
      });
      return;
    }

    setIsUploading(true);

    try {
      const formData = new FormData();
      formData.append('transcript', file);

      const response = await fetch('/api/upload', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Upload failed');
      }

      const result = await response.json();
      setUploadedFile({ name: result.filename, size: result.size });
      onFileUploaded(result.content, result.filename);

      toast({
        title: "File uploaded successfully",
        description: `${result.filename} has been processed`
      });
    } catch (error) {
      console.error('Upload error:', error);
      toast({
        title: "Upload failed",
        description: error instanceof Error ? error.message : "Failed to upload file",
        variant: "destructive"
      });
    } finally {
      setIsUploading(false);
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      handleFileUpload(files[0]);
    }
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
  };

  const removeFile = () => {
    setUploadedFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6" data-testid="file-upload-section">
      <div className="flex items-center space-x-3 mb-4">
        <Upload className="text-primary text-lg" />
        <h2 className="text-lg font-semibold">Step 1: Upload Meeting Transcript</h2>
      </div>

      <div 
        className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center hover:border-primary transition-colors cursor-pointer"
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        onClick={() => fileInputRef.current?.click()}
        data-testid="upload-zone"
      >
        <Upload className="w-12 h-12 text-gray-400 mx-auto mb-4" />
        <p className="text-lg font-medium text-gray-900 mb-2">
          {isUploading ? "Uploading..." : "Drop your transcript file here"}
        </p>
        <p className="text-sm text-gray-500 mb-4">or click to browse files</p>
        <p className="text-xs text-gray-500">Supports .txt, .docx files up to 10MB</p>
        <input
          ref={fileInputRef}
          type="file"
          accept=".txt,.docx"
          className="hidden"
          onChange={(e) => e.target.files && handleFileUpload(e.target.files[0])}
          data-testid="file-input"
        />
      </div>

      {uploadedFile && (
        <div className="mt-4" data-testid="file-preview">
          <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
            <div className="flex items-center space-x-3">
              <FileText className="text-primary" />
              <div>
                <p className="font-medium text-sm">{uploadedFile.name}</p>
                <p className="text-xs text-gray-500">
                  {(uploadedFile.size / 1024).toFixed(1)} KB
                </p>
              </div>
            </div>
            <Button
              variant="ghost"
              size="sm"
              onClick={removeFile}
              className="text-red-500 hover:text-red-700 hover:bg-red-50"
              data-testid="button-remove-file"
            >
              <X className="w-4 h-4" />
            </Button>
          </div>
        </div>
      )}

      <div className="flex justify-end mt-6">
        <Button
          onClick={onNext}
          disabled={!uploadedFile || isUploading}
          className="bg-primary text-white hover:bg-blue-700"
          data-testid="button-continue-to-prompt"
        >
          Continue to Instructions
          <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
          </svg>
        </Button>
      </div>
    </div>
  );
}

