import { CheckCircle } from "lucide-react";

interface ProgressIndicatorProps {
  currentStep: number;
}

const steps = [
  { number: 1, title: "Upload Transcript" },
  { number: 2, title: "Add Instructions" },
  { number: 3, title: "Generate & Edit" },
  { number: 4, title: "Share Summary" }
];

export function ProgressIndicator({ currentStep }: ProgressIndicatorProps) {
  return (
    <div className="mb-8" data-testid="progress-indicator">
      <div className="flex items-center justify-between text-sm">
        {steps.map((step, index) => (
          <div key={step.number} className="flex items-center">
            <div className="flex items-center space-x-2" data-testid={`step-${step.number}`}>
              <div className={`w-8 h-8 rounded-full flex items-center justify-center font-medium ${
                step.number <= currentStep 
                  ? 'bg-primary text-white' 
                  : 'bg-gray-300 text-gray-500'
              }`}>
                {step.number < currentStep ? (
                  <CheckCircle className="w-5 h-5" />
                ) : (
                  step.number
                )}
              </div>
              <span className={`font-medium ${
                step.number <= currentStep ? 'text-primary' : 'text-gray-500'
              }`}>
                {step.title}
              </span>
            </div>
            {index < steps.length - 1 && (
              <div className="flex-1 h-px bg-gray-300 mx-4 hidden md:block"></div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

