import { useState, useEffect } from "react";
import { Edit, ArrowLeft, Sparkles, Lightbulb } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";

interface PromptSectionProps {
  onBack: () => void;
  onGenerate: (prompt: string) => void;
  isGenerating: boolean;
}

export function PromptSection({ onBack, onGenerate, isGenerating }: PromptSectionProps) {
  const [prompt, setPrompt] = useState("Summarize the meeting transcript in bullet points, focusing on key decisions, action items, and next steps. Format it for executive review.");
  const [templates, setTemplates] = useState<Record<string, string>>({});
  const { toast } = useToast();

  useEffect(() => {
    // Load prompt templates
    fetch('/api/templates')
      .then(res => res.json())
      .then(data => setTemplates(data))
      .catch(error => {
        console.error('Failed to load templates:', error);
        toast({
          title: "Failed to load templates",
          description: "Using default template options",
          variant: "destructive"
        });
      });
  }, [toast]);

  const useTemplate = (templateName: string) => {
    const templatePrompt = templates[templateName];
    if (templatePrompt) {
      setPrompt(templatePrompt);
    }
  };

  const handleGenerate = () => {
    if (!prompt.trim()) {
      toast({
        title: "Prompt required",
        description: "Please enter instructions for how to summarize the transcript",
        variant: "destructive"
      });
      return;
    }
    onGenerate(prompt.trim());
  };

  const templateButtons = [
    { key: "Executive Summary Format", icon: "ðŸ“‹", label: "Executive Summary Format" },
    { key: "Action Items Only", icon: "âœ…", label: "Action Items Only" },
    { key: "Project Status Update", icon: "ðŸ“Š", label: "Project Status Update" },
    { key: "Key Decisions & Outcomes", icon: "ðŸŽ¯", label: "Key Decisions & Outcomes" }
  ];

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6" data-testid="prompt-section">
      <div className="flex items-center space-x-3 mb-4">
        <Edit className="text-primary text-lg" />
        <h2 className="text-lg font-semibold">Step 2: Add Custom Instructions</h2>
      </div>

      <div className="space-y-4">
        <div>
          <label htmlFor="prompt-input" className="block text-sm font-medium text-gray-900 mb-2">
            How would you like the transcript summarized?
          </label>
          <Textarea
            id="prompt-input"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            rows={4}
            className="w-full resize-none"
            placeholder="e.g., 'Summarize in bullet points for executives' or 'Highlight only action items and deadlines'"
            data-testid="input-prompt"
          />
        </div>

        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
          <h3 className="font-medium text-blue-900 mb-2">
            <Lightbulb className="w-4 h-4 inline mr-2 text-blue-600" />
            Suggestion Templates
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
            {templateButtons.map((template) => (
              <Button
                key={template.key}
                variant="outline"
                onClick={() => useTemplate(template.key)}
                className="text-left justify-start p-2 text-sm text-blue-700 hover:bg-blue-100 border-blue-200"
                data-testid={`button-template-${template.key.toLowerCase().replace(/\s+/g, '-')}`}
              >
                <span className="mr-2">{template.icon}</span>
                {template.label}
              </Button>
            ))}
          </div>
        </div>
      </div>

      <div className="flex justify-between mt-6">
        <Button
          variant="ghost"
          onClick={onBack}
          className="text-gray-500 hover:text-gray-700"
          data-testid="button-back-to-upload"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Upload
        </Button>
        <Button
          onClick={handleGenerate}
          disabled={isGenerating || !prompt.trim()}
          className="bg-primary text-white hover:bg-blue-700"
          data-testid="button-generate-summary"
        >
          {isGenerating ? (
            <>
              <div className="animate-spin w-4 h-4 mr-2 border-2 border-white border-t-transparent rounded-full"></div>
              Generating...
            </>
          ) : (
            <>
              Generate Summary
              <Sparkles className="w-4 h-4 ml-2" />
            </>
          )}
        </Button>
      </div>
    </div>
  );
}

client/src/components/share-section.tsx.content:
import { useState } from "react";
import { Share, ArrowLeft, Plus, X, Mail, Copy } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";

interface ShareSectionProps {
  summaryId: string;
  summaryContent: string;
  onBack: () => void;
  onSuccess: () => void;
}

export function ShareSection({ summaryId, summaryContent, onBack, onSuccess }: ShareSectionProps) {
  const [recipients, setRecipients] = useState<string[]>([]);
  const [emailInput, setEmailInput] = useState("");
  const [subject, setSubject] = useState("Meeting Summary - AI Generated");
  const [message, setMessage] = useState("");
  const [isSending, setIsSending] = useState(false);
  const { toast } = useToast();

  const addRecipient = () => {
    const email = emailInput.trim();
    if (!email) return;

    // Basic email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      toast({
        title: "Invalid email",
        description: "Please enter a valid email address",
        variant: "destructive"
      });
      return;
    }

    if (recipients.includes(email)) {
      toast({
        title: "Duplicate email",
        description: "This email is already in the list",
        variant: "destructive"
      });
      return;
    }

    setRecipients([...recipients, email]);
    setEmailInput("");
  };

  const removeRecipient = (email: string) => {
    setRecipients(recipients.filter(r => r !== email));
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addRecipient();
    }
  };

  const copyFinalSummary = async () => {
    try {
      // Convert HTML to plain text for copying
      const tempDiv = document.createElement('div');
      tempDiv.innerHTML = summaryContent;
      const textContent = tempDiv.innerText || tempDiv.textContent || '';

      await navigator.clipboard.writeText(textContent);
      toast({
        title: "Summary copied",
        description: "The summary has been copied to your clipboard"
      });
    } catch (error) {
      console.error('Copy error:', error);
      toast({
        title: "Copy failed",
        description: "Failed to copy summary",
        variant: "destructive"
      });
    }
  };

  const sendEmail = async () => {
    if (recipients.length === 0) {
      toast({
        title: "No recipients",
        description: "Please add at least one email recipient",
        variant: "destructive"
      });
      return;
    }

    if (!subject.trim()) {
      toast({
        title: "Subject required",
        description: "Please enter an email subject",
        variant: "destructive"
      });
      return;
    }

    setIsSending(true);

    try {
      const response = await fetch('/api/send-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          summaryId,
          recipients,
          subject: subject.trim(),
          message: message.trim() || undefined
        })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to send email');
      }

      toast({
        title: "Email sent successfully",
        description: `Summary sent to ${recipients.length} recipient${recipients.length > 1 ? 's' : ''}`
      });

      onSuccess();
    } catch (error) {
      console.error('Email sending error:', error);
      toast({
        title: "Failed to send email",
        description: error instanceof Error ? error.message : "Unknown error occurred",
        variant: "destructive"
      });
    } finally {
      setIsSending(false);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6" data-testid="share-section">
      <div className="flex items-center space-x-3 mb-4">
        <Share className="text-primary text-lg" />
        <h2 className="text-lg font-semibold">Step 4: Share Summary via Email</h2>
      </div>

      <div className="space-y-4">
        <div>
          <label htmlFor="email-recipients" className="block text-sm font-medium text-gray-900 mb-2">
            Recipient Email Addresses
          </label>
          <div className="space-y-2">
            <div className="flex space-x-2">
              <Input
                id="email-recipients"
                type="email"
                value={emailInput}
                onChange={(e) => setEmailInput(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Enter email address"
                className="flex-1"
                data-testid="input-email-recipient"
              />
              <Button
                onClick={addRecipient}
                variant="outline"
                className="px-4"
                data-testid="button-add-recipient"
              >
                <Plus className="w-4 h-4" />
              </Button>
            </div>

            {recipients.map((email, index) => (
              <div
                key={index}
                className="flex items-center justify-between p-2 bg-gray-50 rounded border"
                data-testid={`recipient-${index}`}
              >
                <span className="text-sm">{email}</span>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => removeRecipient(email)}
                  className="text-red-500 hover:text-red-700 hover:bg-red-50"
                  data-testid={`button-remove-recipient-${index}`}
                >
                  <X className="w-4 h-4" />
                </Button>
              </div>
            ))}
          </div>
        </div>

        <div>
          <label htmlFor="email-subject" className="block text-sm font-medium text-gray-900 mb-2">
            Email Subject
          </label>
          <Input
            id="email-subject"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
            placeholder="Enter email subject"
            data-testid="input-email-subject"
          />
        </div>

        <div>
          <label htmlFor="email-message" className="block text-sm font-medium text-gray-900 mb-2">
            Additional Message (Optional)
          </label>
          <Textarea
            id="email-message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            rows={3}
            placeholder="Add a personal message to accompany the summary..."
            className="resize-none"
            data-testid="input-email-message"
          />
        </div>

        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
          <div className="flex items-start space-x-2">
