import { useState } from "react";
import { Bot, CheckCircle } from "lucide-react";
import { Button } from "@/components/ui/button";
import { ProgressIndicator } from "@/components/progress-indicator";
import { FileUpload } from "@/components/file-upload";
import { PromptSection } from "@/components/prompt-section";
import { SummaryEditor } from "@/components/summary-editor";
import { ShareSection } from "@/components/share-section";
import { useToast } from "@/hooks/use-toast";

type AppStep = 1 | 2 | 3 | 4 | 5; // 5 = success

export default function Home() {
  const [currentStep, setCurrentStep] = useState<AppStep>(1);
  const [transcriptContent, setTranscriptContent] = useState("");
  const [transcriptFilename, setTranscriptFilename] = useState("");
  const [summaryId, setSummaryId] = useState("");
  const [generatedSummary, setGeneratedSummary] = useState("");
  const [isGenerating, setIsGenerating] = useState(false);
  const { toast } = useToast();

  const handleFileUploaded = (content: string, filename: string) => {
    setTranscriptContent(content);
    setTranscriptFilename(filename);
  };

  const handleGenerateSummary = async (prompt: string) => {
    setIsGenerating(true);

    try {
      const response = await fetch('/api/generate-summary', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          transcript: transcriptContent,
          prompt: prompt
        })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to generate summary');
      }

      const result = await response.json();
      setSummaryId(result.summaryId);
      setGeneratedSummary(result.summary);
      setCurrentStep(3);

      toast({
        title: "Summary generated successfully",
        description: "Your meeting summary is ready for review"
      });
    } catch (error) {
      console.error('Summary generation error:', error);
      toast({
        title: "Failed to generate summary",
        description: error instanceof Error ? error.message : "Unknown error occurred",
        variant: "destructive"
      });
    } finally {
      setIsGenerating(false);
    }
  };

  const handleRegenerateSummary = () => {
    setCurrentStep(2);
    toast({
      title: "Ready to regenerate",
      description: "Modify your instructions and generate a new summary"
    });
  };

  const handleEmailSuccess = () => {
    setCurrentStep(5);
  };

  const startNew = () => {
    setCurrentStep(1);
    setTranscriptContent("");
    setTranscriptFilename("");
    setSummaryId("");
    setGeneratedSummary("");
    setIsGenerating(false);
  };

  return (
    <div className="min-h-screen bg-gray-50" data-testid="home-page">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-4xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center">
                <Bot className="text-white text-sm" />
              </div>
              <h1 className="text-xl font-semibold text-gray-900">AI Meeting Summarizer</h1>
            </div>
            <div className="text-sm text-gray-500 hidden md:block">
              Transform your meeting notes into actionable summaries
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-4xl mx-auto px-4 py-8">
        {/* Progress Indicator */}
        {currentStep < 5 && <ProgressIndicator currentStep={currentStep} />}

        {/* Loading Section */}
        {isGenerating && (
          <div className="mb-8">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <div className="text-center py-8">
                <div className="animate-spin w-12 h-12 border-4 border-primary border-t-transparent rounded-full mx-auto mb-4"></div>
                <h3 className="text-lg font-semibold mb-2">Generating AI Summary...</h3>
                <p className="text-gray-500">This may take a few moments while we process your transcript</p>
              </div>
            </div>
          </div>
        )}

        {/* Step 1: File Upload */}
        {currentStep === 1 && !isGenerating && (
          <FileUpload
            onFileUploaded={handleFileUploaded}
            onNext={() => setCurrentStep(2)}
          />
        )}

        {/* Step 2: Prompt Section */}
        {currentStep === 2 && !isGenerating && (
          <PromptSection
            onBack={() => setCurrentStep(1)}
            onGenerate={handleGenerateSummary}
            isGenerating={isGenerating}
          />
        )}

        {/* Step 3: Summary Editor */}
        {currentStep === 3 && !isGenerating && (
          <SummaryEditor
            summaryId={summaryId}
            initialSummary={generatedSummary}
            onBack={() => setCurrentStep(2)}
            onNext={(editedSummary) => {
              setGeneratedSummary(editedSummary);
              setCurrentStep(4);
            }}
            onRegenerate={handleRegenerateSummary}
          />
        )}

        {/* Step 4: Share Section */}
        {currentStep === 4 && !isGenerating && (
          <ShareSection
            summaryId={summaryId}
            summaryContent={generatedSummary}
            onBack={() => setCurrentStep(3)}
            onSuccess={handleEmailSuccess}
          />
        )}

        {/* Step 5: Success Section */}
        {currentStep === 5 && (
          <div className="mb-8">
            <div className="bg-green-50 border border-green-200 rounded-lg p-6">
              <div className="text-center">
                <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <CheckCircle className="text-green-600 text-2xl w-8 h-8" />
                </div>
                <h3 className="text-lg font-semibold text-green-900 mb-2">Summary Sent Successfully!</h3>
                <p className="text-green-700 mb-4">Your meeting summary has been sent to all recipients.</p>
                <Button
                  onClick={startNew}
                  className="bg-primary text-white hover:bg-blue-700"
                  data-testid="button-start-new"
                >
                  Create New Summary
                </Button>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-white border-t border-gray-200 mt-12">
        <div className="max-w-4xl mx-auto px-4 py-6">
          <div className="text-center text-sm text-gray-500">
            <p>AI Meeting Summarizer - Transform your meetings into actionable insights</p>
            <p className="mt-1">Built for productivity and collaboration</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

